package edu.aitu.algos; import java.util.*; public final class Demo{ public static void main(String[] args){ int[] a=new Random(123).ints(20,-50,50).toArray(); int[] b=a.clone(); QuickSort.sort(a); Arrays.sort(b); System.out.println("QuickSort equals Arrays.sort? "+Arrays.equals(a,b)); int[] c=new Random(456).ints(15,0,100).toArray(); int k=7; int kth=DeterministicSelect.select(c,k); int[] c2=c.clone(); Arrays.sort(c2); System.out.println("DeterministicSelect k="+k+" -> "+kth+" (check: "+c2[k]+")"); ClosestPair.Point[] pts={ new ClosestPair.Point(0,0), new ClosestPair.Point(1,1), new ClosestPair.Point(3,4), new ClosestPair.Point(2,2)}; double d=ClosestPair.solve(pts); System.out.println("Closest pair distance ~ "+d); }}