package edu.aitu.algos; import org.junit.jupiter.api.*; import static org.junit.jupiter.api.Assertions.*; public class ClosestPairTest{ private static double brute(ClosestPair.Point[] pts){ double best=Double.POSITIVE_INFINITY; for(int i=0;i<pts.length;i++){ for(int j=i+1;j<pts.length;j++){ double dx=pts[i].x()-pts[j].x(); double dy=pts[i].y()-pts[j].y(); double d=Math.hypot(dx,dy); if(d<best) best=d; } } return best; } @Test void simpleCase(){ ClosestPair.Point[] pts={ new ClosestPair.Point(0,0), new ClosestPair.Point(3,4), new ClosestPair.Point(1,1), new ClosestPair.Point(2,2) }; double d1=ClosestPair.solve(pts); double d2=brute(pts); assertEquals(d2,d1,1e-9);} }