package edu.aitu.algos; import org.junit.jupiter.api.*; import java.util.*; import java.util.Arrays; import static org.junit.jupiter.api.Assertions.*; public class DeterministicSelectTest{ @Test void smallKnown(){ int[] a={5,1,4,2,8,5,3}; int k=3; int v=DeterministicSelect.select(a,k); int[] copy=a.clone(); Arrays.sort(copy); assertEquals(copy[k],v);} @Test void randomChecks(){ Random rnd=new Random(777); for(int n=1;n<=200;n+=23){ int[] a=rnd.ints(n,-1000,1000).toArray(); int k=n/2; int v=DeterministicSelect.select(a,k); int[] b=a.clone(); Arrays.sort(b); assertEquals(b[k],v);} } }