package edu.aitu.algos; import org.junit.jupiter.api.*; import java.util.*; import java.util.Arrays; import static org.junit.jupiter.api.Assertions.*; public class SortsTest{ @Test void emptyAndSingleton(){ int[] a={}; QuickSort.sort(a); assertArrayEquals(new int[]{},a); int[] b={42}; MergeSort.sort(b); assertArrayEquals(new int[]{42},b);} @Test void randomQuickSortVsJdk(){ Random rnd=new Random(123); int[] a=rnd.ints(5000,-100000,100000).toArray(); int[] b=a.clone(); QuickSort.sort(a); Arrays.sort(b); assertArrayEquals(b,a);} @Test void randomMergeSortVsJdk(){ Random rnd=new Random(321); int[] a=rnd.ints(5000,-100000,100000).toArray(); int[] b=a.clone(); MergeSort.sort(a); Arrays.sort(b); assertArrayEquals(b,a);} }